command:
/**
 * CreateItem Macro
 *
 * To embed this in a Journal, or an Item or Actor description,
 * use the following:
 * 
 * [[/mgMacro CreateItem name="Glass of Wine"]]{Take a drink}
 */
let itemName = "New Item";
let itemType = "item";

if (scope.name) {
  itemName = scope.name;
}
if (scope.type) {
  itemType = scope.type;
}

// Add item to token
for (let t of canvas.tokens.controlled) {
    let actor = t.actor;
    if (actor) {
       const itemData = {
         "name": itemName,
         "img": "",
         "type": itemType
       }
       Item.create(itemData, { parent: actor });
    }
}
