command:
/*
 * Creates a random NPC using the NPC generator roll tables.
 * Copy the "NPC Generator" folder from the "MgT2e Base Tables"
 * compendium into your world, in order to edit your own copy
 * of the generator.
 */

let npcData = {
    "type": "npc",
    "name": "AAA",
    "img": `systems/mgt2e/icons/cargo/passenger-high.svg`,
    "ownership": {
        default: 2
    },
    "system": {
        "settings": {
            hideUntrained: true,
            lockCharacteristics: true
        },
        "sophont": {
            age: 24,
            homeworld: "Bork"
        },
        "description": "Randomly generated"
    }
}
await game.mgt2e.generateNpc(npcData);

Actor.implementation.create(npcData);
