command:
// If a Traveller, remove damage on PSI.
for (let t of canvas.tokens.controlled) {
    let actor = t.actor;
    if (actor) {
        if (actor.system.damage && actor.system.damage["PSI"]) {
            actor.system.damage["PSI"].value = 0;
            actor.update({"system.damage": actor.system.damage});
        }
    }
}
